INSERT INTO %db_prefix%receipt_template (template,is_default,template_name,type) VALUES ('<h1 style="text-align:center;">[clinic_name]</h1><h2 style="text-align:center;">[tag_line]</h2><p style="text-align:center;">[clinic_address]</p><span class="contact"><p style="text-align: center;"><b style="line-height: 1.42857143;">Landline : </b><span style="line-height: 1.42857143;">[landline]</span>  <b style="line-height: 1.42857143;">Mobile : </b><span style="line-height: 1.42857143;">[mobile]</span>  <b style="line-height: 1.42857143;">Email : </b><span style="text-align: center;"> [email]</span></p></span><hr id="null"><span style="text-align: left;"><b>Date : </b>[visit_date] </span><span style="float: right;"><b style="text-align: left;">Patient ID: </b>[patient_id]<br/><b style="text-align: left;">Patient Name: </b>[patient_name]<br/><b style="text-align: left;">Age / Sex: </b>[age] | [sex]</span><h1>Rx</h1><p><br></p><table style="width: 100%;margin-top: 25px;margin-bottom: 25px;border-collapse: collapse;border:1px solid black;"><thead><tr><td style="padding:5px;border:1px solid black;"><b>Medicine Name</b></td><td style="padding:5px;border:1px solid black;"><b>Dosage</b></td><td style="padding:5px;border:1px solid black;"><b>Quantity</b></td><td style="padding:5px;border:1px solid black;"><b>Instructions</b></td></tr></thead><tbody>[col:medicine_name|dosage|quantity|instructions]</tbody></table><p><br></p><p><br></p><p>[doctor_name]</p>', 1, 'Main','prescription');
